		move.w	#$202F,($FFFFF626).w
		bsr	Pal_FadeTo2
		addq.b	#2,($FFFFD0A4).w ; make	title card move
		addq.b	#4,($FFFFD0E4).w ; make	title card move
		addq.b	#4,($FFFFD124).w ; make	title card move
		addq.b	#4,($FFFFD164).w ; make	title card move

Level_StartGame:
		move.w	#$8014,($C00004)	; enable horizontal interrupts (normally only enabled in LZ)
		ints_enable
		bclr	#7,($FFFFF600).w	; clear pre-level sequence flag

; ---------------------------------------------------------------------------
; Main level loop (when	all title card and loading sequences are finished)
; ---------------------------------------------------------------------------

Level_MainLoop:
		bsr	RandomNumber		; constantly create a new random number every frame to make use of RandomNumber_Next
		bsr	CinematicScreenFuzz	; do cinematic screen fuzz if applicable

		bsr	PauseGame
		addq.w	#1,($FFFFFE04).w ; add 1 to level timer
		move.b	#8,VBlankRoutine
		bsr	DelayProgram
		bsr	LZWaterEffects
		jsr	ObjectsLoad

		tst.w	($FFFFFE02).w		; is the level set to restart?
		bne.w	Level			; if yes, branch

		bsr	DeformBgLayer
		jsr 	LevelRenderer_Update_FG
		jsr 	LevelRenderer_Update_BG
		jsr	BuildSprites
		jsr	ObjPosLoad
		tst.b	($FFFFFFAC).w
		bne.s	L_ML_NoPalCycle
		bsr	PalCycle_Load

L_ML_NoPalCycle:
		jsr	ObjPosLoad
		bsr	PLC_Execute
		bsr	OscillateNumDo
		bsr	ChangeRingFrame
		bsr	SignpostArtLoad

L_ML_NoSAL:
		cmpi.b	#$C,($FFFFF600).w
		beq.w	Level_MainLoop	; if screen mode is $0C	(level), branch
		rts	
