		tst.b	($FFFFF7EF).w	; has switch $F	been pressed?
		beq.s	loc_6F28	; if not, branch
		lea 	LevelLayout_FG+($40*2)+6, a1
		cmpi.b	#7,(a1)
		beq.s	loc_6F28
		move.b	#7,(a1)		; modify level layout
		move.w	#$B7,d0
		bsr	PlaySound_Special ; play rumbling sound

