		move.w	#$1FC0,($FFFFD008).w
		move.w	#0,($FFFFD010).w

		cmpi.w	#$6A0,($FFFFD00C).w
		bcs.s	@cont2x
		subq.w	#8,($FFFFFFB6).w
		move.w	($FFFFFFB6).w,($FFFFD012).w
		move.b	#2,($FFFFD01C).w
		bra.s	@cont3x
		
@cont2x:
		cmpi.w	#$8C,($FFFFD00C).w
		bcc.s	@cont4x
		addq.w	#8,($FFFFFFB6).w
		tst.w	($FFFFFFB6).w
		bmi.s	@cont5x
		move.w	#-8,($FFFFFFB6).w

@cont5x:
		move.w	($FFFFFFB6).w,($FFFFD012).w
		move.b	#2,($FFFFD01C).w
		bra.s	@cont3x
@cont4x:
