RandomDirection:
		bsr	RandomNumber_Next
		andi.l	#$01FF01FF,d0
		subi.w	#$FF,d0
		move.w	d0,obVelX(a0)
		swap	d0
		subi.w	#$FF,d0
		move.w	d0,obVelY(a0)
		rts

