Obj0F:					; XREF: Obj_Index
		moveq	#0,d0
		move.b	obRoutine(a0),d0
		move.w	Obj0F_Index(pc,d0.w),d1
		jsr	obj0F_Index(pc,d1.w)
		bra.w	DisplaySprite
