		cmpi.b	#1,($FFFFFE10).w
		bne.s	loc_BF16
		move.w	#$43DE,obGfx(a0)	; LZ specific code

loc_BF16:
		move.b	#4,obRender(a0)
		move.b	#3,obPriority(a0)
		move.w	obX(a0),$34(a0)
		move.w	obY(a0),$36(a0)
