		move.b	(a2)+,obActWid(a0)
		move.b	(a2)+,obFrame(a0)
		tst.b	obSubtype(a0)
		beq.s	Obj33_ChkGone
		move.w	#$C2B8,obGfx(a0)

Obj33_ChkGone:
		lea	($FFFFFC00).w,a2
		moveq	#0,d0
		move.b	obRespawnNo(a0),d0
		beq.s	loc_BF6E
		bclr	#7,obGfx(a2,d0.w)
		bset	#0,obGfx(a2,d0.w)
		bne.w	DeleteObject

loc_BF6E:				; XREF: Obj33_Index
		tst.b	$32(a0)
