Obj40_Move:				; XREF: Obj40_Index2
	;	jsr	obj40_CheckSonicPos
		subq.w	#1,$30(a0)	; subtract 1 from pause	time
		bpl.s	locret_F70A	; if time remains, branch		
		addq.b	#2,ob2ndRout(a0)
		;move.w	#-$100,obVelX(a0)	; move object to the left
		move.b	#1,obAnim(a0)
		;bchg	#0,obStatus(a0)
		;bne.s	locret_F70A
		;neg.w	obVelX(a0)		; change direction

locret_F70A:
		rts	
; ===========================================================================

Obj40_CheckSonicPos:
		tst.w	($FFFFFE08).w	; is debug mode	on?
		beq.s	@cont		; if not, branch
		clr.w	obVelX(a0)
		bra.s	BugMoveEnd	; make Motobug ignore you
@cont:
		moveq	#0,d0
		move.w	($FFFFD008).w,d0
		sub.w	$08(a0),d0
		bpl.s	BugMoveRight
		bclr	#0,obStatus(a0)		
		move.w	#-MotoBugSpeed,obVelX(a0)	; move object to the left
		bra.s	BugMoveEnd

BugMoveRight:
		bset	#0,obStatus(a0)
		move.w	#MotoBugSpeed,obVelX(a0)	; move object to the right

BugMoveEnd:
		rts
; ===========================================================================
Obj40_FixToFloor:			; XREF: Obj40_Index2
		tst.b	($FFFFFFB1).w
		bmi.s	Obj40_NotInhumanCrush
		tst.b	obRender(a0)
		bpl.b	Obj40_NotInhumanCrush
		move.b	#6,obRoutine(a0)
		bsr	SingleObjLoad
		bne.s	Obj40_NotInhumanCrush
		move.b	#$3F,0(a1)
		move.w	obX(a0),obX(a1)
		move.w	obY(a0),obY(a1)

Obj40_NotInhumanCrush:
		tst.b	$32(a0)
		beq.s	Obj40_DoCheck
	;	btst	#1,($FFFFD022).w
	;	bne.s	BugMoveReturn
		moveq	#0,d0
		move.w	($FFFFD008).w,d0
		sub.w	$08(a0),d0
		bpl.s	BugMoveRightX
		btst	#0,obStatus(a0)
		bne.s	Obj40_DoCheckX
		bra.s	BugMoveEndX
; ===========================================================================

BugMoveRightX:
		btst	#0,obStatus(a0)
		beq.s	Obj40_DoCheckX

BugMoveEndX:
		move.b	#0,obAnim(a0)
BugMoveReturn:
		rts
; ===========================================================================

Obj40_DoCheckX:
		move.b	#1,obAnim(a0)
		clr.b	$32(a0)

Obj40_DoCheck:
		jsr	obj40_CheckSonicPos
		bsr	SpeedToPos
		jsr	ObjHitFloor
		cmpi.w	#-8,d1
		blt.s	Obj40_Pause
		cmpi.w	#$C,d1
		bge.s	Obj40_Pause
		add.w	d1,obY(a0)	; match	object's position with the floor
		subq.b	#1,$33(a0)
		bpl.s	locret_F756
		move.b	#$F,$33(a0)
		bsr	SingleObjLoad
		bne.s	locret_F756
		move.b	#$40,0(a1)	; load exhaust smoke object
		move.w	obX(a0),obX(a1)
		move.w	obY(a0),obY(a1)
		move.b	obStatus(a0),obStatus(a1)
		move.b	#2,obAnim(a1)

locret_F756:
		rts	
; ===========================================================================

Obj40_Pause:				; XREF: Obj40_FixToFloor
	;	jsr	obj40_CheckSonicPos
		subq.b	#2,ob2ndRout(a0)
		move.w	#59,$30(a0)	; set pause time to 1 second
		move.w	#0,obVelX(a0)	; stop the object moving
		move.b	#0,obAnim(a0)
		move.b	#1,$32(a0)
		rts	
; ===========================================================================

Obj40_Animate:				; XREF: Obj40_Index
	;	jsr	obj40_CheckSonicPos
		lea	(Ani_obj40).l,a1
		bsr	AnimateSprite
		bra.w	DisplaySprite
; ===========================================================================

Obj40_Delete:				; XREF: Obj40_Index
	;	jsr	obj40_CheckSonicPos
		bra.w	DeleteObject
