; ===========================================================================
; ---------------------------------------------------------------------------
BombWalkSpeed_Boss = $140
BombFuseTime_Boss = 41
BombFuseVelocity_Boss = $70*2
BombDistance_Boss = $50
BombPellets_Boss = 13
; ---------------------------------------------------------------------------
; ===========================================================================

Obj5F:					; XREF: Obj_Index
		cmpi.w	#$301,($FFFFFE10).w
		beq.s	@0
		move.b	#$3F,(a0)
		move.b	#0,obRoutine(a0)
		rts
@0:
		moveq	#0,d0
		move.b	obRoutine(a0),d0
		move.w	Obj5F_Index(pc,d0.w),d1
		jmp	Obj5F_Index(pc,d1.w)
; ===========================================================================
Obj5F_Index:	dc.w Obj5F_Main-Obj5F_Index		; [$0]
		dc.w Obj5F_Action-Obj5F_Index		; [$2]
		dc.w Obj5F_Display-Obj5F_Index		; [$4]
		dc.w Obj5F_End-Obj5F_Index		; [$6]
		dc.w Obj5F_SBZ1-Obj5F_Index		; [$8]
		dc.w Obj5F_BossDefeated-Obj5F_Index	; [$A]
		dc.w Obj5F_Display_Spark-Obj5F_Index	; [$C]
		dc.w Obj5F_Transition-Obj5F_Index	; [$E]
; ===========================================================================

Obj5F_Main:				; XREF: Obj5F_Index
		addq.b	#2,obRoutine(a0)
		move.l	#Map_obj5F,obMap(a0)
	;	move.l	#Map_obj5F_Giant,obMap(a0)
		move.w	#$400,obGfx(a0)
	;	move.w	#$42C,obGfx(a0)
		ori.b	#4,obRender(a0)
		move.b	#3,obPriority(a0)
		move.b	#$C,obActWid(a0)

		cmpi.w	#$301,($FFFFFE10).w
		bne.s	@conty
		tst.b	($FFFFFFA9).w
		beq.s	@conty
		tst.b	($FFFFFF75).w
		bne.s	@conty
		move.b	#$A,obRoutine(a0)
		bra.w	Obj5F_BossDefeated 

@conty:
		
		cmpi.w	#$500,($FFFFFE10).w
		bne.s	@cont
		move.b	#8,obRoutine(a0)
		move.w	#$533,obGfx(a0)
		move.l	#Map_obj5F_Cutscene,obMap(a0)
		move.b	#$CD,d0
		jsr	PlaySound_Special
		bra.w	Obj5F_SBZ1

@cont:
		move.b	obSubtype(a0),d0
		beq.s	loc_11A3C
		move.b	d0,obRoutine(a0)
		rts	
; ===========================================================================

loc_11A3C:
		move.b	#$9A,obColType(a0)
		bchg	#0,obStatus(a0)

Obj5F_Action:				; XREF: Obj5F_Index
		moveq	#0,d0
		move.b	ob2ndRout(a0),d0
		move.w	Obj5F_Index2(pc,d0.w),d1
		jsr	Obj5F_Index2(pc,d1.w)
		lea	(Ani_obj5F).l,a1
		jsr	AnimateSprite

		move.w	obX(a0),d0
		andi.w	#$FF80,d0
		move.w	($FFFFF700).w,d1
		subi.w	#$80,d1
		andi.w	#$FF80,d1
		sub.w	d1,d0
		cmpi.w	#$280,d0
		bhi.w	DeleteObject
		bra.w	DisplaySprite
; ===========================================================================
Obj5F_Index2:	dc.w Obj5F_Walk-Obj5F_Index2
		dc.w Obj5F_Wait-Obj5F_Index2
		dc.w Obj5F_Explode-Obj5F_Index2
; ===========================================================================

Obj5F_Walk:				; XREF: Obj5F_Index2
		bsr.w	Obj5F_ChkSonic
		bmi	locret_11AD0
		move.b	#0,obAnim(a0)
		moveq	#0,d0
		move.w	($FFFFD008).w,d0
		sub.w	obX(a0),d0
		bpl.s	Obj5F_Walk2
		bclr	#0,obStatus(a0)

		move.w	#-BombWalkSpeed_Boss,obVelX(a0)	; move object to the left
		move.b	#1,obAnim(a0)
		cmpi.b	#2,($FFFFFFA9).w
		blt	Obj5F_Return
		moveq	#0,d0
		moveq	#0,d1
		move.b	($FFFFFF75).w,d0
		move.b	#20,d1
		sub.b	d0,d1
		lsl.w	#4,d1
		sub.w	d1,obVelX(a0)
		bra.s	Obj5F_Return

Obj5F_Walk2:
		bset	#0,obStatus(a0)
	;	clr.w	obVelX(a0)			; disable walking for stationary bombs
	
		move.w	#BombWalkSpeed_Boss,obVelX(a0)	; move object to the left
		moveq	#0,d0
		moveq	#0,d1
		move.b	($FFFFFF75).w,d0
		move.b	#20,d1
		sub.b	d0,d1
		lsl.w	#4,d1
		add.w	d1,obVelX(a0)
		move.b	#1,obAnim(a0)

Obj5F_Return:
		bsr	ObjHitFloor
		subi.w	#15,d1
		cmpi.b	#3,obAnim(a0)
		beq.s	@contx

@cont:
		add.w	d1,obY(a0)	; match	object's position with the floor
		bsr.w	SpeedToPos
@contx:
		rts
; ===========================================================================

Obj5F_FaceSonic:
		moveq	#0,d0
		move.w	($FFFFD008).w,d0
		sub.w	obX(a0),d0
		bpl.s	Obj5F_FC2
		bclr	#0,obStatus(a0)		
		rts

Obj5F_FC2:
		bset	#0,obStatus(a0)
		rts
; ===========================================================================

Obj5F_Wait:				; XREF: Obj5F_Index2
		bsr.w	Obj5F_ChkSonic
		subq.w	#1,$30(a0)	; subtract 1 from time delay
		bmi.s	loc_11AA8
		bsr.w	SpeedToPos
		rts	
; ===========================================================================

loc_11AA8:
		subq.b	#2,ob2ndRout(a0)
		move.w	#0,$30(a0)	; set time delay to 3 seconds
		clr.w	obVelX(a0)		; stop walking
		rts	
; ===========================================================================

Obj5F_Explode:				; XREF: Obj5F_Index2
		move.b	#0,obAnim(a0)	; stop animation
		move.b	#4,obPriority(a0)
		subq.w	#1,$30(a0)
		bpl.w	locret_11AD0

		subq.b	#1,($FFFFFF75).w		; remove one life
		move.b	($FFFFFF75).w,($FFFFFF68).w	; update boss health in HUD
		move.b	#1,($FFFFFE1C).w		; update lives

		tst.b	($FFFFFF75).w			; has bomb boss been defeated?
		bhi.s	@notdefeated			; if not, branch
		clr.w	($FFFFD010).w			; clear Sonic's X speed
		clr.w	($FFFFD014).w			; clear Sonic's intertia
		move.b	#1,($FFFFF7CC).w		; lock controls
		move.b	#1,($FFFFFE2D).w		; make Sonic invincible
		clr.l	($FFFFF602).w			; clear any remaining button presses
		clr.w	obVelX(a0)			; clear bomb's X-speed
	;	move.b	#$E,obRoutine(a0)		; set routine (WIP transition)
		move.b	#$A,obRoutine(a0)		; set routine
		rts

@notdefeated:
		moveq	#10,d0		; add 100 ...
		jsr	AddPoints	; ... points

		jsr	SingleObjLoad
		move.b	#$5F,0(a1)	; load a new bomb object
		move.w	obX(a0),obX(a1)
		move.w	obY(a0),obY(a1)
		move.w	obStatus(a0),obStatus(a1)

		move.b	#$3F,0(a0)	; change bomb into an explosion
		move.b	#0,obRoutine(a0)

		cmpi.b	#1,($FFFFFFA9).w
		bne.s	locret_11AD0
		
		; stuff that gets run when the bomb boss starts for real
		move.b	#2,($FFFFFFA9).w
		move.b	#4,($FFFFD024).w
		move.b	#$1A,($FFFFD01C).w
		bset	#1,($FFFFD022).w
		move.w	#-$200,($FFFFD010).w
		subq.w	#1,($FFFFD008).w
		move.w	#-$400,($FFFFD012).w
		move.b	#$9B,d0				; set boss music
		jsr	PlaySound			; play it
		move.b	#10,($FFFFFF64).w		; camera shaking
		clr.b	($FFFFF7CC).w			; unlock controls 1

locret_11AD0:
		rts	
; ===========================================================================

Obj5F_ChkSonic:				; XREF: Obj5F_Walk; Obj5F_Wait
		bsr	Obj5F_FaceSonic
		move.w	($FFFFD008).w,d0
		sub.w	obX(a0),d0
		bcc.s	loc_11ADE
		neg.w	d0

loc_11ADE:
		move.w	#BombDistance,d1
		tst.b	($FFFFFFA9).w
		beq.s	@cont
		move.w	#BombDistance_Boss,d1
@cont:
		cmp.w	d1,d0
		bcc.w	locret_11B5E

		move.w	($FFFFD00C).w,d0
		sub.w	obY(a0),d0
		bcc.s	Obj5F_MakeFuse
		neg.w	d0

Obj5F_MakeFuse:
	;	move.w	#BombDistance,d1
		move.w	#$90,d1
		tst.b	($FFFFFFA9).w
		beq.s	@contx
		move.w	#BombDistance_Boss,d1
@contx:
		cmp.w	d1,d0
