loc_11B54:
		move.w	#BombFuseTime,$30(a1)	; set fuse time
		tst.b	($FFFFFFA9).w
		beq.s	@cont
		move.w	#BombFuseTime_Boss,$30(a1)	; set fuse time
@cont:
