@StartSequence:
		addq.b	#2, $FFFFFF76
		
		; fade out music
		move.b	#$E0, d0
		jsr		PlaySound_Special

		; clear shit
		bset	#0, $FFFFD022
		clr.w	$FFFFFF8C
		clr.w	$FFFFFF8E
		clr.b	$FFFFFFEB
		rts

@CheckAirborne:
		btst	#1, $FFFFD022		; test airborne bit
		bne.s	@NotAirborne		; if 0, branch
		rts

@NotAirborne:
		addq.b	#2, $FFFFFF76		; continue with sequence

@Explode:
		addq.b	#2, $FFFFFF76

		; not my code, blame selbi
		bsr	SingleObjLoad
		move.b	#$3F,0(a1)	; load explosion object
		move.w	obX(a0),obX(a1)
		move.w	obY(a0),obY(a1)
		move.b	#1,$30(a1)
		jsr	(RandomNumber_Next).l

		; move.w	d0,d1
		; moveq	#0,d1
		; move.b	d0,d1
		; lsr.b	#2,d1
		; subi.w	#$20,d1
		; add.w	d1,obX(a1)
		; lsr.w	#8,d0
		; lsr.b	#3,d0
		; add.w	d0,obY(a1)
		; subq.w	#8,obY(a1)
		move.b	#10,($FFFFFF64).w
		bra.w 	@Return

@CheckExplosions:
		cmpi.w	#10,($FFFFFF7A).w
		ble.w	@ContinueBoss

		move.w	($FFFFFF7A).w,($FFFFFF78).w
		subq.b	#4,($FFFFFF76).w
		subi.w	#10,($FFFFFF7A).w
		move.w	#20,($FFFFFF7C).w
		bra.w	@Return

@ContinueBoss:
		move.b	#21,($FFFFFF75).w	; set lives
		move.b	#$2, obRoutine(a0)
		move.b	#0, $FFFFFF76
		rts

@Return:
