Obj5F_Display:				; XREF: Obj5F_Index
	;	tst.b	($FFFFFFA9).w		; is Sonic fighting against the walking bomb?
	;	beq.w	@conty			; if not, don't generate bonus sparkles
		cmpi.b	#3,obAnim(a0)
		bne.s	@conty
		move.b	($FFFFFE05).w,d0
		andi.b	#$0B,d0
		tst.b	d0
		bne.s	@conty
		jsr	SingleObjLoad
		bne.s	@conty
		move.b	#$5F,0(a1)	; load fuse object (bonus sparkles)
		move.w	obX(a0),obX(a1)
		move.w	obY(a0),obY(a1)
		move.b	#$C,obSubtype(a1)	; set to routine $C
		move.b	#5,obAnim(a1)
		move.b	#1,obPriority(a1)

		btst	#1,obStatus(a0)
		beq.s	@0
		addi.w	#$60,obY(a1)

@0:		
		jsr	RandomNumber_Next
		andi.l	#$01FF01FF,d0
		subi.w	#$FF,d0
		asl.w	#1,d0
		move.w	d0,obVelX(a1)
		swap	d0
		subi.w	#$FF,d0
		move.w	d0,obVelY(a1)
		move.w	$30(a0),d0
		lsr.w	#1,d0
		move.w	#BombFuseTime_Boss,d1
		sub.w	d0,d1
		move.w	d1,$30(a1)	; set fuse time

@conty:
		cmpi.b	#5,obAnim(a0)
		bne.s	@contx
		subi.w	#10,obVelY(a0)
		tst.b	($FFFFFFA9).w
		beq.s	@contx
		tst.b	($FFFFFF75).w
		bne.s	@contx
		jmp	DeleteObject

@contx:
	;	cmpi.w	#$301,($FFFFFE10).w
	;	bne.s	@cont
		cmpi.b	#3,obAnim(a0)
		bne.s	@cont
		tst.b	($FFFFFF76).w
		beq.s	@cont
		jmp	DeleteObject
@cont:
		bsr	Obj5F_FaceSonic
		bsr.s	loc_11B70
		lea	(Ani_obj5F).l,a1
		jsr	AnimateSprite
		bra.w	MarkObjGone
