		bsr	Sonic_Jump
		bsr	Sonic_SlopeResist
		bsr	Sonic_Move
		bsr	Sonic_Roll
		bsr	Sonic_LevelBound
		bsr	SpeedToPos
		bsr	Sonic_AnglePos
		bsr	Sonic_SlopeRepel
		bsr	Sonic_Fire
		rts	
