		bsr	Sonic_AirFreeze
		bsr	Sonic_JumpHeight
		bsr	Sonic_ChgJumpDir
		bsr	Sonic_LevelBound
		bsr	ObjectFall_Sonic
		btst	#6,obStatus(a0)	; is Sonic underwater?
		beq.s	loc_12E5C	; if not, branch
