		jsr	AnimateSprite
		cmpi.b	#2,obFrame(a0)
		bne.s	Obj4A_Display
		tst.b	($FFFFD000).w
		beq.s	Obj4A_Display
		move.b	#0,($FFFFD000).w ; remove Sonic
