		move.b	d0,d1
		lsr.b	#2,d1
		subi.w	#$20,d1
		add.w	d1,obX(a1)
		lsr.w	#8,d0
		lsr.b	#3,d0
		add.w	d0,obY(a1)

loc_1ACA0:
		subq.w	#1,obTimeFrame(a0)
		beq.s	Obj3E_MakeNuke
		rts	
; ===========================================================================

Obj3E_MakeNuke:
		move.b	#2,($FFFFF7A7).w
		move.b	#$C,obRoutine(a0)
		move.b	#6,obFrame(a0)
		move.w	#$96,obTimeFrame(a0)
		addi.w	#$20,obY(a0)
		
		; load nuke object
		jsr	SingleObjLoad
		bne.s	locret_1ACF8
		move.b	#$28,0(a1)
		move.w	obX(a0),obX(a1)
		move.w	obY(a0),obY(a1)

locret_1ACF8:
		rts	
; ===========================================================================

Obj3E_Nuke:				; XREF: Obj3E_Index
		subq.w	#1,obTimeFrame(a0)
		bne.s	locret_1AD48
		addq.b	#2,obRoutine(a0)
		move.w	#180,obTimeFrame(a0)
		move.b	#1,($FFFFFFA5).w	; move HUD off screen (and start FZEscape sequence)

locret_1AD48:
		rts	
; ===========================================================================

Obj3E_EndAct:				; XREF: Obj3E_Index
		rts
; ===========================================================================
Ani_obj3E:
		include	"_anim\obj3E.asm"

