		move.l	obY(a0),d2
		move.l	obX(a0),d3
		move.b	($FFFFF780).w,d0
		andi.b	#$FC,d0
		jsr	(CalcSine).l
	;	move.w	obVelX(a0),d4
		moveq	#0,d4
		ext.l	d4
		asl.l	#8,d4
	;	muls.w	#$2A,d0
