		asl.l	#8,d4
	;	muls.w	#$2A,d1
		muls.w	#$160,d1
		add.l	d4,d1
		add.l	d0,d3
		bsr	Obj09_Collision
		beq.w	loc_1BCB0
		sub.l	d0,d3
		moveq	#0,d0
