Hud_ClrLives:
		tst.w	d6
		beq.w	loc_1CAA6
		moveq	#7,d5

Hud_ClrLivesLoop:
		move.l	#0,(a6)
		dbf	d5,Hud_ClrLivesLoop
		bra.w	loc_1CABC
